postgres=# CREATE TABLESPACE temp_tablespc LOCATION '/path/to/existing/desired/mount/point';              

postgres=# ALTER SYSTEM SET temp_tablespaces = 'temp_tablespc';              

postgres=# SELECT pg_reload_conf();
